import React from 'react'
import { Row, Col, Input, Form, Button, Layout } from 'antd';
import "antd/dist/antd.css";
import Router from 'next/router'

class Student extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            firstName: "Minhaj ahmad khan",
            lastName: "",
            age: ""

        }
    }
    onSubmit = (e) => {
        e.preventDefault();
        if ((this.state.age == "") && (this.state.firstName == "") && (this.state.lastName == "")) {
            alert("Invalid Arguments")
        }
        else if ((this.state.age < 18) && (this.state.firstName != "") && (this.state.lastName != "")) {
            alert("you are under 18 ")
            this.setState({ firstName: "" })
            this.setState({ lastName: "" })
            this.setState({ age: "" })

        }
        else {
            Router.push('/success')
        }

    }
    changesuccess = (item) => {
        this.setState({ firstName: item })
    }

    render() {
        return (
            <>    <div>

                <Layout data={
                    {
                        firstName: this.state.firstName.firstName, changesuccess: this.changesuccess
                    }
                } />
            </div>

                <Form className="ant - advanced - search - form" >

                    <label htmlFor="First Name">First Name</label>
                    <Input
                        placeholder="Username"
                        required
                        value={this.state.firstName}
                        onChange={(e) => {
                            this.setState({ firstName: e.target.value })
                        }}
                    />

                    <label htmlFor="Last Name">Last Name</label>
                    <Input

                        type="text"
                        placeholder="Last Name"
                        required={true}
                        value={this.state.lastName}
                        onChange={(e) => {
                            this.setState({ lastName: e.target.value })
                        }}

                    />

                    <label htmlFor="Age">Age</label>
                    <Input
                        type="text"
                        placeholder="Age"
                        required={true}
                        value={this.state.age}
                        onChange={(e) => {
                            this.setState({ age: e.target.value })
                        }}
                    />
                    <div>
                        <center>
                            <Button className="" type="primary" htmlType="submit" onClick={this.onSubmit} >
                                Save
                          </Button>
                        </center>
                    </div>
                </Form>
                <style jsx>
                    {`
                        .ant - advanced - search - form {
                            padding: 24px;
                            background: #fbfbfb;
                            border: 1px solid #d9d9d9;
                            border-radius: 6px;
                        }
                    
                         .ant-advanced-search-form .ant-form-item {
                            display: flex;
                        }
                                    
                        .ant-advanced-search-form .ant-form-item-control-wrapper {
                             flex: 1;
                       }
                    `}
                </style>
            </>

        )
    }
}
export default Student
